targets.expr:
avg by (provider, api_method, source_region) (
  avg_over_time(
    response_latency_seconds{blockchain="Ethereum", response_status="success", api_method=~"(eth_call|eth_getBalance)"}[$__range]
  )
)

avg by (provider, api_method, source_region) (
  avg_over_time(
    response_latency_seconds{blockchain="Ethereum", response_status="success", api_method=~"(eth_blockNumber|eth_getTransactionReceipt)"}[$__range]
  )
)

avg by (provider, api_method, source_region) (
  avg_over_time(
    response_latency_seconds{blockchain="Ethereum", response_status="success", api_method=~"(.*debug.*|eth_subscribe)"}[$__range]
  )
)

sort(  # Using sort instead of sort_desc since lower is better now
  1 / (
    avg by (provider) (
      (
        1 / avg by (provider, source_region) (
          avg_over_time(
            response_latency_seconds{blockchain="Ethereum", response_status="success"}[$__range]
          )
        )
      )
      *
      (
        (
          sum by (provider, source_region) (rate(response_latency_seconds{blockchain="Ethereum", response_status="success"}[$__range]))
          /
          sum by (provider, source_region) (rate(response_latency_seconds{blockchain="Ethereum"}[$__range]))
        )
        *
        (
          sum by (provider, source_region) (rate(response_latency_seconds{blockchain="Ethereum", response_status="success"}[$__range]))
          /
          sum by (provider, source_region) (rate(response_latency_seconds{blockchain="Ethereum"}[$__range]))
        )
        *
        (
          sum by (provider, source_region) (rate(response_latency_seconds{blockchain="Ethereum", response_status="success"}[$__range]))
          /
          sum by (provider, source_region) (rate(response_latency_seconds{blockchain="Ethereum"}[$__range]))
        )
      )
    )
  )
)
